        ? "MM/dd/yyyy"
        : "dd/MM/yyyy";
    System.Globalization.DateTimeFormatInfo dtfi =
        System.Globalization.CultureInfo.CurrentCulture.DateTimeFormat;
    dtfi.DateSeparator = "/";

    return Json(new
    {
        iTotalDisplayRecords = displayRecords,
        iTotalRecords = totalRecords,
        sEcho = gridRequest.GridCustomData,
        aaData =
            allturnosMaster.Select(
                d =>
                    new
                    {
d.Turno_Numero,d.Turno_Descripcion,d.Turno_Hora_Desde,d.Turno_Hora_Hasta,d.Turno_Minutos_Cantidad,d.Turno_Cantidad_Publicadores,d.Registro_Estado,Registro_Fecha = d.Registro_Fecha.ToString(dateFormat, dtfi),d.Registro_Usuario,Horario_Numero = d.HorariosMaster != null ? d.HorariosMaster.EntityDisplayName : string.Empty
                    })
    });

}

